import Chart from "react-apexcharts";
import { useContext } from "react"; // Import useContext
import { GlobalContext } from "../../context"; // Import GlobalContext
import { Box, Flex, Heading, Text } from "@chakra-ui/react"; // Import necessary Chakra UI components

export default function TransactionChartSummary() {
  const { allTransactions } = useContext(GlobalContext); // Get allTransactions from context

  // Filter out transactions with amount 0 to avoid issues in chart rendering if any
  const validTransactions = allTransactions.filter(transaction => transaction.amount > 0);

  // Prepare data for ApexCharts
  const seriesData = validTransactions.map(transaction => transaction.amount); // Transaction amounts for series
  const labelsData = validTransactions.map(transaction => transaction.description); // Transaction descriptions for labels
  const colorsData = validTransactions.map(transaction =>
    transaction.type === "expense" ? "#FD5E53" : "#213ebf" // Dynamic colors based on transaction type
  );

  const options = {
    labels: labelsData, // Use transaction descriptions as labels
    colors: colorsData, // Use dynamically generated colors
    chart: {
      width: "100%", // Use a responsive width
    },
    states: {
      hover: {
        filter: {
          type: "none",
        },
      },
    },
    legend: {
      show: true, // Show legend to identify transactions
      position: 'bottom', // Position legend at the bottom
    },
    dataLabels: {
      enabled: true, // Enable data labels to show values/percentages
      formatter: function (val, opts) {
        return opts.w.config.series[opts.seriesIndex] + " - " + val.toFixed(1) + "%";
      },
    },
    hover: { mode: null },
    plotOptions: {
      pie: { // For pie chart, use 'pie' instead of 'donut' directly
        expandOnClick: false,
        donut: {
          labels: {
            show: true, // Show labels on the donut slices
            name: {
              show: true,
            },
            value: {
              show: true,
              formatter: function (val) {
                return "$" + val; // Format value to show currency
              },
            },
            total: {
                show: true,
                label: 'Total',
                formatter: function (w) {
                    const total = w.globals.series.reduce((a, b) => a + b, 0);
                    return "$" + total.toFixed(2);
                }
            }
          },
        },
      },
    },
    fill: {
      colors: colorsData, // Use dynamically generated colors for fill
    },
    tooltip: {
      enabled: true,
      theme: "dark",
      style: {
        fontSize: "12px",
        fontFamily: undefined,
        backgroundColor: "#000000",
      },
      y: { // Custom tooltip formatter to show amount
          formatter: function (val) {
              return "$" + val
          }
      }
    },
  };

  return (
    <Box
      flex={1}
      w="full"
      display={"flex"}
      alignItems={"center"}
      justifyContent={"center"}
      h={"100%"}
      minW={"300px"}
      maxW={"400px"}
    >
      <Flex flexDir={"column"} alignItems={"center"} justifyContent={"center"} h={"100%"}>
        <Heading size={"md"} color={"red.700"}>
          Transaction Distribution
        </Heading>
        {validTransactions && validTransactions.length > 0 ? ( // Conditionally render chart
          <Chart
            options={options}
            series={seriesData} // Pass the array of transaction amounts
            type="pie" // Specify type as "pie"
            width={"100%"}
            height={"100%"}
          />
        ) : (
          <Text mt={4}>No transactions to display in chart.</Text>
        )}
      </Flex>
    </Box>
  );
}
