import { Box, Flex, Heading, Text } from "@chakra-ui/react";
import { useContext } from "react"; // Import useContext
import { FaTrash } from "react-icons/fa";
import { GlobalContext } from "../../context"; // Import GlobalContext

export default function ExpenseView({ type, data }) {
  const { deleteTransaction } = useContext(GlobalContext); // Destructure deleteTransaction

  return (
    <Box
      flex={1}
      w="full"
      bg={"white"}
      mr={"4"}
      mt={"10"}
      p={"5"}
      pb={"4"}
      border={"1px solid"}
      borderColor={"gray.100"}
      borderRadius={"12"}
    >
      <Flex justifyContent={"space-between"} alignItems={"center"}>
        <Heading size={"md"} color={"red.700"}>
          {type === "income" ? "Income" : "Expense"}
        </Heading>
      </Flex>
      {data.map((item) => (
        <>
          <Flex
           key={item.id} // Add a key for list rendering optimization
            onClick={() => deleteTransaction(item.id)}
            bg={type === "expense" ? "red.50" : "blue.50"}
            mt={"4"}
            justifyContent={"space-between"}
            alignItems={"center"}
            border={"1px solid"}
            borderColor={type === "expense" ? "red.100" : "blue.100"}
            p={"4"}
            borderRadius={"8"}

          >
            <Flex alignItems={"center"} justifyContent={"center"}>
              <Text ml="3" fontWeight="bold" color="gray.600">
                {item.description}
              </Text>
            </Flex>
            <Flex alignItems={"center"} justifyContent={"center"}> {/* New Flex to align amount and button */}
                <Text>$ {item.amount}</Text>
                {/* Add the delete button here */}
                <IconButton
                    icon={<FaTrash />} // Using a trash icon. Make sure to install 'react-icons' if you haven't (npm install react-icons)
                    colorScheme="red"
                    size="sm"
                    ml="4" // Margin left for spacing
                    onClick={(e) => {
                        e.stopPropagation(); // Prevent the parent Flex's onClick (if any) from firing
                        deleteTransaction(item.id);
                    }}
                    aria-label="Delete transaction"
                />
            </Flex>
          </Flex>
        </>
      ))}
    </Box>
  );
}
