import Chart from "react-apexcharts";
import { useContext } from "react";
import { GlobalContext } from "../../context";

// Utility to generate random colors
function getRandomColors(n) {
  const colors = [];
  for (let i = 0; i < n; i++) {
    const color = `hsl(${Math.floor(Math.random() * 360)}, 70%, 60%)`;
    colors.push(color);
  }
  return colors;
}

export default function TransactionChartSummary() {
  const { allTransactions } = useContext(GlobalContext);

  const labels = allTransactions.map((txn) => txn.description || "Unnamed");
  const series = allTransactions.map((txn) => parseFloat(txn.amount));
  const colors = getRandomColors(allTransactions.length);

  const options = {
    labels,
    colors,
    legend: {
      show: true,
      position: "bottom",
    },
    chart: {
      type: "donut",
    },
    dataLabels: {
      enabled: false,
    },
    tooltip: {
      enabled: true,
      theme: "dark",
    },
    plotOptions: {
      pie: {
        expandOnClick: true,
      },
    },
  };

  return (
    <Chart
      options={options}
      series={series}
      type="donut"
      width={"100%"}
      height={"100%"}
    />
  );
}
